import React from "react";
import Testa from "./componentCard/Test";
import './componentCard/style/style.css'
import add from "./componentCard/data"

export function setData(key, arr) {
  Array.isArray(arr) ? localStorage.setItem(key, JSON.stringify(arr)) : console.log("error");

}
export function getData(arr) {
  return localStorage.getItem(arr) ? JSON.parse(localStorage.getItem(arr)) : [];
}


function App(props) {

  let data = getData("Datalists")
  return (
    <div className="d-flex wrap w-100 mt-3">
      <div id="list" className="main d-flex flex-wrap">
        {
          add().map(arr => 
            <Testa props={props.ii}  tit={arr.title} mess={arr.mess} name={arr.name} /> 

          )
        }
      </div>
    </div>
  );
}

export default App;
