import React from 'react';
import PropTypes from 'prop-types';
import { useState } from 'react';


function Testa(props) {
  const { tit, name, mess } = props;
  const [status, setTus] = useState("Start");
  const [color, setcolor] = useState('color :#04BE00');
  const [text, settext] = useState("New");

  function settxt() {
    if (status === "Start" && text === "New") {
      setTus('Done'),
      setcolor('color :#F88F14'),
      settext('Doning')
    } else if (status === "Done" && text === "Doing") {
      setTus('Renew'),
      setcolor('color :#675BF1'),
      settext('Done') 
    }
  };
  return (
    <div className="taskItem mx-2 my-2 px-2 py-2">
      <label> Title : <a> {tit}</a> </label><br></br>
      <label> Creator : <a>{name} </a></label><br></br>
      <label> <a style={color}>Status: {text} </a></label><br></br>
      <p>
        Desscription:<br /> {mess}
      </p>
      <button onClick={settxt()}>{status}</button>
    </div>

  )
};
Testa.propTypes = {

}
Testa.defaultProps = {
  tit: "title 1",
  name: "name",
  status: "status 1",
  mess: "messs",
}



export default Testa;
