  Array.isArray(arr) ? localStorage.setItem(key, JSON.stringify(arr)): console.log("error");

}
function getData(arr){
  return localStorage.getItem(key) ? JSON.parse(localStorage.getItem(key)) : [];
}


function App() {
  return (
    <div className="d-flex wrap w-100 mt-3">
      <div className="main d-flex flex-wrap">     
      {        
      add().map(arr=> <Testa stt={arr.stt}  tit={arr.title} mess={arr.mess} name={arr.name}/>        
      )
      }
      </div>
    </div>
  );
}

export default App;
